package Week2;

public class StringManipulation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String str = ("I love Java but I dont like to JavaScript and Jquery");
		
		String str2 = "Hend fathalla";
		
		// index   ==0;
		
		//length == to find length
		
		System.out.println(str.length());
		
		// index number 
		
		//charAt()==to find particular index in string 
		
		System.out.println(str.charAt(3));
		System.out.println(str.charAt(30));
		
	//indexOf ()find character index	
	System.out.println(str.indexOf("like"));
	
	}

}
