package Week3;

public class RelatioanlOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * ==
		 * >
		 * <
		 * >=
		 * <=
		 *! 
		 *!= 
		 * 
		 */
		
		int a = 10;
		int b =25;
		int c = 25;
		
	//>greater than 	
		
	System.out.println("Greater than");	
	System.out.println("a>b = "+(a>b));
	System.out.println("b>a = "+(a>b));
	System.out.println("c>b = "+(a>b));	
	System.out.println("");	
	
	//greater than equal
	
	System.out.println("Greater than");	
	System.out.println("a>=b = "+(a>=b));
	System.out.println("b>=a = "+(a>=b));
	System.out.println("c>=b = "+(a>=b));	
	System.out.println("");	
	
	//less than
	System.out.println("Greater than");	
	System.out.println("a<b = "+(a<b));
	System.out.println("b<a = "+(a<b));
	System.out.println("c<b = "+(a<b));	
	System.out.println("");
	
	//equal
	System.out.println("Greater than");	
	System.out.println("a==b = "+(a==b));
	System.out.println("b==a = "+(a==b));
	System.out.println("c==b = "+(a==b));	
	System.out.println("");
	
	//not equal to
	System.out.println("Greater than");	
	System.out.println("a!=b = "+(a!=b));
	System.out.println("b!=a = "+(a!=b));
	System.out.println("c!=b = "+(a!=b));	
	System.out.println("");
	
	
	}

}
